
public class OperatoriDiAssegnamento {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int somma = 1000;
		int n = 10;
		
		somma += n+1;
		System.out.println(somma);
		
		somma = 1000;
		n = 10;
		
		somma -= n;
		System.out.println(somma);
		
		somma = 1000;
		n = 10;		
		
		somma *= n;
		System.out.println(somma);
		
		somma = 1000;
		n = 10;
		
		somma /= n;
		System.out.println(somma);
		
		somma = 1000;
		n = 10;
		
		//fornisce il modulo
		System.out.println(n %= 7);
		
	}

}
